"use strict";

require("@polkadot/types/injector");

var _storage = require("./storage");

// Copyright 2017-2019 @polkadot/api-metadata authors & contributors
// This software may be modified and distributed under the terms
// of the Apache-2.0 license. See the LICENSE file for details.
describe('storage', () => {
  it('should return well known keys', () => {
    expect(typeof _storage.storage.substrate).toBe('object'); // @deprecated: The ':auth:' (authorityPrefix) and ':auth:len' (authorityCount) storage keys
    // have been removed in https://github.com/paritytech/substrate/pull/2802

    expect(_storage.storage.substrate.authorityCount).toBeTruthy();
    expect(_storage.storage.substrate.authorityPrefix).toBeTruthy();
    expect(_storage.storage.substrate.changesTrieConfig).toBeTruthy();
    expect(_storage.storage.substrate.childStorageKeyPrefix).toBeTruthy();
    expect(_storage.storage.substrate.code).toBeTruthy();
    expect(_storage.storage.substrate.extrinsicIndex).toBeTruthy();
    expect(_storage.storage.substrate.heapPages).toBeTruthy();
  });
});