describe('createClass', () => {
  it('should memoize from strings', () => {
    const a = (0, _.createClass)('BabeWeight');
    const b = (0, _.createClass)('BabeWeight');
    expect(a).toBe(b);
  });
  it('should return equivalents for Bytes & Vec<u8>', () => {
    const A = (0, _.createClass)('Vec<u8>');
    const B = (0, _.createClass)('Bytes');
    expect(new A() instanceof B).toBe(true);
  });
});